// src/components/ReserveAlert.js
import React, { useState, useEffect } from "react";

const ReserveAlert = () => {
  const [reserveKm, setReserveKm] = useState("");
  const [alertTime, setAlertTime] = useState("6");
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    const savedLogs = JSON.parse(localStorage.getItem("reserveLogs")) || [];
    setLogs(savedLogs);
  }, []);

  const handleSave = () => {
    const newLog = {
      km: reserveKm,
      date: new Date().toLocaleString(),
    };

    const updatedLogs = [...logs, newLog];
    localStorage.setItem("reserveLogs", JSON.stringify(updatedLogs));
    setLogs(updatedLogs);

    // Also store alert time separately if needed
    localStorage.setItem("alertTime", alertTime);
    alert("âœ… Reserve alert saved!");
    setReserveKm(""); // clear input
  };

  return (
    <div style={{ padding: "20px" }}>
      <h3>ðŸ”” Reserve Details & Alert</h3>

      <input
        type="number"
        placeholder="Reserve Kilometers"
        value={reserveKm}
        onChange={(e) => setReserveKm(e.target.value)}
      />
      <br />

      <label>Alert me after: </label>
      <select value={alertTime} onChange={(e) => setAlertTime(e.target.value)}>
        <option value="6">6 hrs</option>
        <option value="12">12 hrs</option>
        <option value="24">24 hrs</option>
      </select>
      <br />

      <button onClick={handleSave} style={{ marginTop: 10 }}>
        Save Reserve Alert
      </button>

      {logs.length > 0 && (
        <div style={{ marginTop: 20 }}>
          <h4>ðŸ“˜ Reserve Log History</h4>
          <table border="1" cellPadding="6" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>S.No</th>
                <th>Date</th>
                <th>Reserved KM</th>
              </tr>
            </thead>
            <tbody>
              {logs.map((log, idx) => (
                <tr key={idx}>
                  <td>{idx + 1}</td>
                  <td>{log.date}</td>
                  <td>{log.km}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ReserveAlert;
