// src/components/ReserveAlert.js import React, { useState, useEffect } from "react";

const ReserveAlert = () => { const [reserveKM, setReserveKM] = useState(""); const [alertAfter, setAlertAfter] = useState("6 hrs"); const [saved, setSaved] = useState(null);

useEffect(() => { const savedData = JSON.parse(localStorage.getItem("reserveAlert")); if (savedData) setSaved(savedData); }, []);

const handleSave = () => { if (!reserveKM) return alert("Please enter Reserve KM"); const now = new Date(); const reserveDate = now.toLocaleDateString("en-GB"); // e.g. 14/07/2025 const reserveTime = now.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });

const data = {
  reserveKM,
  alertAfter,
  reserveDate,
  reserveTime,
};

localStorage.setItem("reserveAlert", JSON.stringify(data));
setSaved(data);
alert("âœ… Reserve details saved!");

};

return ( <div> <h3>ðŸ”” Reserve Details & Alert</h3> <input type="number" placeholder="Enter Reserve KM" value={reserveKM} onChange={(e) => setReserveKM(e.target.value)} />

<div style={{ marginTop: "10px" }}>
    Alert me after:
    <select value={alertAfter} onChange={(e) => setAlertAfter(e.target.value)}>
      <option>6 hrs</option>
      <option>12 hrs</option>
      <option>24 hrs</option>
    </select>
  </div>

  <button onClick={handleSave} style={{ marginTop: "10px" }}>Save Reserve Alert</button>

  {saved && (
    <div style={{ marginTop: "15px" }}>
      <p>âœ… Saved Reserve: <strong>{saved.reserveKM}</strong> km | Alert in <strong>{saved.alertAfter}</strong></p>
      <p>ðŸ“… On: {saved.reserveDate} at {saved.reserveTime}</p>
    </div>
  )}
</div>

); };

export default ReserveAlert;
